import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { X, MapPin, Truck, TrendingUp, BarChart, Route as RouteIcon } from 'lucide-react';
import { ReportRow } from '@/hooks/useReportGenerator';
import IndiaMap from './IndiaMap';

interface TripDetailModalProps {
  tripData: ReportRow | null;
  onClose: () => void;
}

const DetailItem = ({ icon: Icon, label, value }: { icon: React.ElementType, label: string, value: string | number }) => (
  <div className="flex items-start text-sm">
    <Icon className="h-4 w-4 mr-3 mt-1 text-muted-foreground" />
    <div>
      <p className="font-semibold text-gray-800">{label}</p>
      <p className="text-muted-foreground">{value}</p>
    </div>
  </div>
);

const TripDetailModal = ({ tripData, onClose }: TripDetailModalProps) => {
  if (!tripData) return null;

  return (
    <div className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center p-4 animate-in fade-in-0">
      <Card className="w-full max-w-4xl max-h-[90vh] grid grid-cols-1 md:grid-cols-2 relative animate-in zoom-in-95">
        <Button
          variant="ghost"
          size="icon"
          onClick={onClose}
          className="absolute top-2 right-2 rounded-full"
        >
          <X className="h-5 w-5" />
        </Button>
        
        {/* Left Side: Details */}
        <div className="flex flex-col border-r">
          <CardHeader>
            <CardTitle>Trip Summary</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4 overflow-y-auto">
            <DetailItem icon={Truck} label="Vehicle No." value={tripData['Vehicle No.']} />
            <DetailItem icon={TrendingUp} label="Vehicle Category" value={tripData['Vehicle Category']} />
            <DetailItem icon={MapPin} label="Source" value={tripData['Source']} />
            <DetailItem icon={MapPin} label="Destination" value={tripData['Destination']} />
            <DetailItem icon={RouteIcon} label="Distance" value={`${tripData['Running Distance (km)'].toFixed(2)} km`} />
            <DetailItem icon={BarChart} label="Carbon Emissions" value={`${tripData['Calculated Carbon Emissions (kg CO₂e)'].toFixed(2)} kg CO₂e`} />
          </CardContent>
        </div>

        {/* Right Side: Map */}
        <div className="flex flex-col">
          <CardHeader>
            <CardTitle>Route Visualization</CardTitle>
          </CardHeader>
          <CardContent className="flex-grow">
            <IndiaMap origin={tripData.Source} destination={tripData.Destination} />
          </CardContent>
        </div>
      </Card>
    </div>
  );
};

export default TripDetailModal;
